<template>
    <v-card
        v-bind="$attrs"
        v-on="$listeners"
        class="hb-card-wrapper"
        :elevation="elevation"
    >
        <template v-for="(_, slot) of $scopedSlots" v-slot:[slot]="scope"><slot :name="slot" v-bind="scope"/></template>

        <v-row no-gutters class="px-4 hb-card-title d-flex align-center" v-if="!noTitle">
            <span v-if="title && !hasTitleSlot">
                {{ title }}
            </span>
            <slot name="title"></slot>
        </v-row>
        
        <v-divider v-if="!noTitle"></v-divider>

        <slot></slot>
