<script type="text/babel">

export default {
    name:'HbCard',
    data(){
        return {

        }
    },
    computed: {
        hasTitleSlot(){
            return !!this.$slots['title'];
        },
    },
    props: {
        elevation: {
            default: '1'
        },
        title: {
            type: String,
            default: undefined
        },
        noTitle: {
            type: Boolean,
            default: false
        }
    }
}
