}
.v-application a.hb-text-destroy-status, .hb-text-destroy-status {
  color: #FB4C4C;
  text-decoration:none;
}
.v-application a.hb-link-disabled, .hb-link-disabled {
  color: #919EAB;
  text-decoration:none;
  cursor:default;
}
.v-application a.hb-link, .hb-link {
  color: #00848E;
  text-decoration:none;
}

</style>